        System.out.println("显示车辆信息：\n车辆名称为：" + this.name + " 吨位是：" + this.load + "油量是：" + this.oil + " 车损度为：" + this.loss);

    }

    //获取车名

    public String getName()

    {

    return name;

    }

    //获取油量

    public int getOil()

    {

    return oil;

    }

    //获取车损度

    public int getLoss()

    {

    return loss;

    }

    //获取吨位

    public String getLoad()

    {

    return load;

    }

    //加油

    public void addOil()

    {

