        Vehicle car = new Car("战神","长城");                 //初始化轿车对象car

        Vehicle truck = new Truck("大力士二代","10吨");          //初始化卡车对象truck

        Driver d1 = new Driver("柳海龙");                    //创建并初始化驾驶员对象

